mapper.select:
select

from inlong_stream

    is_deleted = 0

        and inlong_group_id = #{groupId, jdbcType=VARCHAR}


        and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

select count(1)
from inlong_stream
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0

select distinct stream.id, stream.inlong_group_id, stream.inlong_stream_id, stream.name, stream.description, stream.mq_resource, stream.data_type, stream.data_encoding, stream.data_separator, stream.data_escape_char, stream.sync_send, stream.daily_records, stream.daily_storage, stream.peak_records, stream.max_length, stream.storage_period, stream.status, stream.creator, stream.modifier, stream.create_time, stream.modify_time, stream.version from inlong_stream stream, inlong_group grp
stream.is_deleted = 0 and stream.inlong_group_id = grp.inlong_group_id and grp.is_deleted = 0
and stream.inlong_group_id = #{request.inlongGroupId, jdbcType=VARCHAR}
and ( find_in_set(#{request.currentUser, jdbcType=VARCHAR}, grp.in_charges) or find_in_set(#{request.currentUser, jdbcType=VARCHAR}, grp.followers) )
and ( stream.inlong_stream_id like CONCAT('%', #{request.keyword}, '%') or stream.name like CONCAT('%', #{request.keyword}, '%') )
and stream.status = #{request.status, jdbcType=INTEGER}
order by stream.${request.orderField} ${request.orderType}
order by stream.create_time desc

mapper.update:
update inlong_stream
set status = #{status, jdbcType=INTEGER},
modifier = #{modifier, jdbcType=VARCHAR}
where inlong_group_id = #{groupId, jdbcType=VARCHAR}

    and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

and is_deleted = 0

update inlong_stream
set is_deleted = id,
    modifier   = #{operator, jdbcType=VARCHAR}
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0
