mapper.select:
select

from inlong_stream_field
where inlong_group_id = #{groupId, jdbcType=VARCHAR}

    and inlong_stream_id = #{streamId, jdbcType=VARCHAR}

and is_deleted = 0
order by id asc

mapper.update:
update inlong_stream_field
set is_deleted = id
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0

mapper.delete:
delete
from inlong_stream_field
where inlong_group_id = #{groupId, jdbcType=VARCHAR}
  and inlong_stream_id = #{streamId, jdbcType=VARCHAR}
  and is_deleted = 0
